import { useState, useEffect } from "react";
import axios from "axios";

const UserProfile = () => {
  const [User, setUser] = useState();
  const [user_name, setUser_name] = useState("");
  const [user_email, setUser_email] = useState("");
  const [user_phone, setUser_phone] = useState("");
  const [user_address, setUser_address] = useState("");
  const [current_password, setCurrent_password] = useState("");
  const [new_password, setNew_password] = useState("");
  const [confirm_password, setConfirm_password] = useState("");
  const [errors, setErrors] = useState({});

  const [activeTab, setActiveTab] = useState("nav-home");

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };
  const getUser = async () => {
    const token = localStorage.getItem("token") || "";
    let cus = {};

    try {
      const response = await axios.get(`http://localhost:8000/get-user-data`, {
        headers: {
          authorization: `Bearer ${token}`,
        },
      });

      cus = response.data;

      setUser(cus);
    } catch (error) {
      console.error(error);
    }
    return cus;
  };
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate form inputs
    const validationErrors = validateForm({ user_name, user_email, user_phone , user_address});
    setErrors(validationErrors);

    // // If there are errors, stop the submission
    // if (Object.keys(validationErrors).length > 0) {
    //   return;
    // }

    // // Create an object with the form data
    // const formData = {
    //   name,
    //   email,
    //   password,
    //   phone,
    //   address,
    //   role: userRole ? "user" : "serviceProvider",
    // };

    // try {
    //   // Send the form data to the server
    //   const response = await axios.post(
    //     "http://localhost:8000/register",
    //     formData
    //   );

    //   console.log("Data sent successfully");

    //   // Save the token to local storage
    //   localStorage.setItem("token", response.data.token);

    //  updateIsLogin(true);

    //   console.log("Data sent successfully");
    //   setErrorResponse(""); // Clear any previous error response
    //   // setMessage('Registration successful'); // Set success message

    //   // Reset form fields
    //   setName("");
    //   setEmail("");
    //   setPassword("");
    //   setPhone("");
    //   setAddress("");
    //   setUserRole(false);
    //   setServiceProviderRole(false);
    //   setErrors({});
    //   navigate("/");
    // } catch (error) {
    //   console.log("Error:", error.message);
    //   setErrorResponse(JSON.stringify(error.response)); // Set error response
    //   // setMessage(''); // Clear success message
    // }
  };

  const validateForm = (values) => {
    // const errors = {};
    // if (!values.name.trim()) {
    //   errors.name = "Name is required";
    // }
    // if (!values.password.trim()) {
    //   errors.password = "Password is required";
    // } else {
    //   if (!/(?=.*[a-z])/.test(values.password)) {
    //     errors.password = "Password must include at least one lowercase letter";
    //   }
    //   if (!/(?=.*[A-Z])/.test(values.password)) {
    //     errors.password = "Password must include at least one uppercase letter";
    //   }
    //   if (!/(?=.*\d)/.test(values.password)) {
    //     errors.password = "Password must include at least one digit";
    //   }
    //   if (!/(?=.*\W)/.test(values.password)) {
    //     errors.password = "Password must include at least one special character";
    //   }
    //   if (values.password.length < 9) {
    //     errors.password = "Password must be at least 9 characters long";
    //   }
    // }
    // if (!values.email.trim()) {
    //   errors.email = "Email is required";
    // } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)) {
    //   errors.email = "Invalid email address";
    // } else if (!/^[A-Z0-9._%+-]+@([A-Z0-9-]+\.)+[A-Z]{2,}$/i.test(values.email)) {
    //   errors.email = "Invalid email domain";
    // }
    // if (!values.phone.trim()) {
    //   errors.phone = "Phone is required";
    // } else if (!/^\d{10}$/i.test(values.phone)) {
    //   errors.phone = "Invalid phone number";
    // }
    // if (!values.address.trim()) {
    //   errors.address = "Address is required";
    // }
    // return errors;
  };

  useEffect(() => {
    getUser();
  }, []);

  return (
    <div className="container mt-2">
      <div className="row flex-lg-nowrap">
        <div className="col">
          <div className="row">
            <div className="col mb-3">
              <div className="card">
                <div className="card-body">
                  <div className="e-profile">
                    <div className="row">
                      <div className="col-12 col-sm-auto mb-3">
                        <div className="mx-auto" style={{ width: 140 }}>
                          <div
                            className="d-flex justify-content-center align-items-center rounded"
                            style={{
                              height: 140,
                              backgroundColor: "rgb(233, 236, 239)",
                            }}
                          >
                            <span
                              style={{
                                color: "rgb(166, 168, 170)",
                                fontWeight: "bold",
                                fontSize: "8pt",
                              }}
                            >
                              <img
                                src="./images/profile.png"
                                width="100"
                                height="100"
                              ></img>
                            </span>
                          </div>
                        </div>
                      </div>
                      <div className="col d-flex flex-column flex-sm-row justify-content-between mb-3">
                        <div className="text-center text-sm-start mb-2 mb-sm-0">
                          <h4 className="pt-sm-2 pb-1 mb-0 text-nowrap">
                            {User?.user_name}
                          </h4>
                          <p className="mb-0"> {User?.user_email} </p>
                        </div>
                        <div className="text-center text-sm-end">
                          <span className="badge bg-secondary">
                            {User?.role == "serviceProvider"
                              ? "Service Provider"
                              : "User"}
                          </span>
                          <div className="text-muted">
                            <small>
                              Joind at {User?.create_at.substring(0, 10)}
                            </small>
                          </div>
                        </div>
                      </div>
                    </div>

                    <div>
                      <nav>
                        <div
                          className="nav nav-tabs"
                          id="nav-tab"
                          role="tablist"
                        >
                          <button
                            className={`nav-link ${
                              activeTab === "nav-home" ? "active" : ""
                            }`}
                            id="nav-home-tab"
                            data-bs-toggle="tab"
                            data-bs-target="#nav-home"
                            type="button"
                            role="tab"
                            aria-controls="nav-home"
                            aria-selected={activeTab === "nav-home"}
                            onClick={() => handleTabClick("nav-home")}
                          >
                            Personal Info
                          </button>
                          <button
                            className={`nav-link ${
                              activeTab === "nav-profile" ? "active" : ""
                            }`}
                            id="nav-profile-tab"
                            data-bs-toggle="tab"
                            data-bs-target="#nav-profile"
                            type="button"
                            role="tab"
                            aria-controls="nav-profile"
                            aria-selected={activeTab === "nav-profile"}
                            onClick={() => handleTabClick("nav-profile")}
                          >
                            Password
                          </button>
                        </div>
                      </nav>
                      <div className="tab-content mt-3" id="nav-tabContent">
                        <div
                          className={`tab-pane fade ${
                            activeTab === "nav-home" ? "show active" : ""
                          }`}
                          id="nav-home"
                          role="tabpanel"
                          aria-labelledby="nav-home-tab"
                        >
                          <form
                            onSubmit={handleSubmit}
                            className="form"
                            noValidate=""
                          >
                            <div className="row">
                              <div className="col-8">
                              <div className="mb-4">
                                <b>Change Personal Info</b>
                              </div>
                                <div className="row">
                                  <div className="col">
                                    <div className="row">
                                      <div className="col">
                                        <div className="form-group">
                                          <label htmlFor="user_name">
                                            Full Name
                                          </label>
                                          <input
                                            className="form-control"
                                            type="text"
                                            id="user_name"
                                            name="user_name"
                                            onChange={(e) =>
                                              setUser_name(e.target.value)
                                            }
                                            placeholder={User?.user_name}
                                            defaultValue={User?.user_name}
                                          />
                                        </div>
                                      </div>
                                      <div className="col">
                                        <div className="form-group">
                                          <label htmlFor="user_phone">
                                            Phone
                                          </label>
                                          <input
                                            className="form-control"
                                            type="text"
                                            id="user_phone"
                                            name="user_phone"
                                            onChange={(e) =>
                                              setUser_phone(e.target.value)
                                            }
                                            placeholder={User?.user_phone}
                                            defaultValue={User?.user_phone}
                                          />
                                        </div>
                                      </div>
                                    </div>
                                    <div className="row">
                                      <div className="col">
                                        <div className="form-group">
                                          <label htmlFor="user_email">
                                            Email
                                          </label>
                                          <input
                                            className="form-control"
                                            type="text"
                                            name="user_email"
                                            id="user_email"
                                            onChange={(e) =>
                                              setUser_email(e.target.value)
                                            }
                                            placeholder={User?.user_email}
                                            defaultValue={User?.user_email}
                                          />
                                        </div>
                                      </div>
                                    </div>
                                    <div className="row">
                                      <div className="col mb-3">
                                        <div className="form-group">
                                          <label htmlFor="user_address">
                                            Address
                                          </label>
                                          <textarea
                                            className="form-control"
                                            id="user_address"
                                            onChange={(e) =>
                                              setUser_address(e.target.value)
                                            }
                                            rows={5}
                                            placeholder={User?.user_address}
                                            defaultValue={User?.user_address}
                                          />
                                        </div>
                                      </div>
                                    </div>
                                  </div>
                                </div>
                              </div>
                              <div className="col-4 d-flex justify-content-end ">
                                <div className="row">
                                  <div className="col-12">
                                    <button
                                      className="btn btn-primary"
                                      type="submit"
                                    >
                                      Save Changes
                                    </button>
                                  </div>
                                </div>
                              </div>
                            </div>
                          </form>
                        </div>
                        <div
                          className={`tab-pane fade ${
                            activeTab === "nav-profile" ? "show active" : ""
                          }`}
                          id="nav-profile"
                          role="tabpanel"
                          aria-labelledby="nav-profile-tab"
                        >
                          <div className="row">
                            <div className="col-8">
                              <div className="mb-4">
                                <b>Change Password</b>
                              </div>
                              <div className="row">
                                <div className="col">
                                  <div className="form-group">
                                    <label htmlFor="current_password">
                                      Current Password
                                    </label>
                                    <input
                                      className="form-control"
                                      type="password"
                                      id="current_password"
                                      name="current_password"
                                      onChange={(e) =>
                                        setCurrent_password(e.target.value)
                                      }
                                      placeholder="••••••"
                                    />
                                  </div>
                                </div>
                              </div>
                              <div className="row">
                                <div className="col">
                                  <div className="form-group">
                                    <label htmlFor="new_password">
                                      New Password
                                    </label>
                                    <input
                                      className="form-control"
                                      type="password"
                                      id="new_password"
                                      name="new_password"
                                      onChange={(e) =>
                                        setNew_password(e.target.value)
                                      }
                                      placeholder="••••••"
                                    />
                                  </div>
                                </div>
                              </div>
                              <div className="row">
                                <div className="col">
                                  <div className="form-group">
                                    <label htmlFor="confirm_password">
                                      Confirm Password
                                    </label>
                                    <input
                                      className="form-control"
                                      type="password"
                                      id="confirm_password"
                                      name="confirm_password"
                                      onChange={(e) =>
                                        setConfirm_password(e.target.value)
                                      }
                                      placeholder="••••••"
                                    />
                                  </div>
                                </div>
                              </div>
                            </div>
                            <div className="col-4 d-flex justify-content-end ">
                              <div className="row">
                                <div className="col-12">
                                  <button
                                    className="btn btn-primary"
                                    type="submit"
                                  >
                                    Save Changes
                                  </button>
                                </div>
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>

                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
